const mapApi = async (data) => {

  const apiData =[];
  const objects = Object.keys(data.near_earth_objects)
  objects.map(day => {
    const objectsForTheDay = data.near_earth_objects[day];

    objectsForTheDay.map(day => {
      return (
        apiData.push({
          id: day.id,
          name: day.name, 
          missDistance: day.close_approach_data[0].miss_distance.lunar,
          avgSize: (day.estimated_diameter.meters.estimated_diameter_min + day.estimated_diameter.meters.estimated_diameter_max) /2
        })
      )
    })
  })


 return apiData
};


 export default mapApi;
