import React,{useContext} from 'react'
import { StoreContext } from "../../context/StoreContext";
import { Bar } from 'react-chartjs-2'

const Chart = () => {

    const { store } = useContext(StoreContext);

    const data = store.data.near_earth_objects;
let arr =[];
    Object.keys(store.data.near_earth_objects).map(day => {
        const objectsForTheDay = data[day];
        objectsForTheDay.map(day => {
           return (
               arr.push({
                   id: day.id,
                   name: day.name, 
                   missDistance: day.close_approach_data[0].miss_distance.kilometers,
                   avgSize: (day.estimated_diameter.kilometers.estimated_diameter_min + day.estimated_diameter.kilometers.estimated_diameter_max) /2
                })
           )
       })
    })



    console.log("arr",arr)


  return (
    <div>
      <Bar 
      height={400}
      width={600}
      labels={'Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'}
      data= {12, 19, 3, 5, 2, 3}
      />
    </div>
  )
}

export default Chart;
