import React, { useState,useContext } from 'react'
import { StoreContext } from "../../context/StoreContext";
import Button from '../Button/Button';

const  Table = () =>{

  const { store } = useContext(StoreContext);  
  const [order, setOrder] = useState("ASC");
  const [data, setData] = useState(store.data);

  const sorting = (col) => {
    if (order === "ASC") {
       const sorted = [...data].sort((a,b) =>
      a[col] > b[col] ? 1 :-1);
      setOrder("DSC");
      setData(sorted);
    }
    if (order === "DSC") {
      const sorted = [...data].sort((a,b) =>
      a[col] < b[col] ? 1 :-1);
      setOrder("ASC");
      setData(sorted);
    }
  }

  return (
    <div>
      <table className="striped highlight centered">
        <thead className="teal lighten-2">
          <tr>
          <th>Index</th>
          <th>Name</th>
          <th >   
          <a  className="text-black" onClick={() => sorting('missDistance')}>Miss Distance Lunar<i class="material-icons right">import_export</i>
          </a>
          </th>
          <th>
            <Button
              text='Average Size'
              type='button'
              className={"btn teal lighten-2 black-text"}
              onClick={() => sorting('avgSize')}
            />
          </th>
          <th>Close Approach Date</th>
          <th onClick={() => sorting('speed')}>
            <Button
              text='Relative Velocity in KM/h'
              type='button'
              className={"btn teal lighten-2 black-text"}
              onClick={() => sorting('speed')}
            />
          </th>
          <th>URL</th>
          </tr>
        </thead>
          <tbody>
            {data.map((asteroid,idx) => {
              return (
                <tr 
                  className="centered"
                  key={asteroid.id}
                >
                  <td> {idx+1}</td>
                  <td>{asteroid.name}</td>
                  <td>{asteroid.missDistance}</td>
                  <td>{asteroid.avgSize}m</td>
                  <td>{asteroid.closeApproachDate}</td>
                  <td>{asteroid.speed}</td>
                  <td><a href={asteroid.url}>Link</a></td>
                </tr>
              );
            })}
          </tbody>
      </table>
    </div>
  )
}

export default Table;
