import React, { useState } from "react";
import Button from '../Button/Button';
import TextInput from '../TextInput/TextInput';
import { Navigate } from "react-router-dom";

const Home = () => {
  const [state, setState] = useState({
    api: "",
    error:"",
    data: {}
  });


  const handleSubmit = (event) => {
    event.preventDefault();
    sendApiRequest(state.api)
    console.log("yeap")
  };

  const handleChange = (event) => {
    setState({
      ...state,
      [event.target.name]: event.target.value,
    });
  };

  let todayDate = new Date().toISOString().slice(0, 10);
  let sevenDaysAgo = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString().slice(0, 10);

  async function sendApiRequest(api){
    console.log("hihi")



    const response = await fetch(`https://api.nasa.gov/neo/rest/v1/feed?start_date=${sevenDaysAgo}&=${todayDate}&api_key=${api}`);
    const data = await response.json();
console.log(data)

    if (response.status !== 200) {
      return setState({ ...state, error: "API KEY is wrong, please try again"})
    } else {
        setState({...state, data: data, error: ""})
        return <Navigate to='/chart' />
    }
  }

  return(
    <div className='row'>
      <div className="col s12 m7">
        <div className="card">
          <div className='card-header'> 
            <h2 className="header">NASA API</h2>
            Hhx2SH25TTzX5z1IhTqIUtaVYbzr1AKWeC6KpGqu
          </div>
        <div className="card-stacked">
          <div className="card-content">
            <form onSubmit={handleSubmit}>
              <TextInput
                type='text'
                name='api'
                className='validate'
                value={state.api}
                onChange={handleChange}
                htmlFor='NASA API'
                label='NASA API'
              />         
              <Button
                text='submit'
                type='submit'
                className={
                  !state.api ? "btn disabled"
