import React,{useContext} from 'react'
import { StoreContext } from "../../context/StoreContext";
import { Line } from 'react-chartjs-2'
import Chart from 'chart.js/auto';
import Table from '../Table/Table';

const ChartPage = () => {

  const { store } = useContext(StoreContext);  
  let orderedArray = store.data.sort((a,b) => a.missDistance - b.missDistance);
  const xArrayData = orderedArray.map(arry => arry.avgSize.toFixed(2));
  const yArrayData = orderedArray.map(arry => parseFloat(arry.missDistance).toFixed(2));

  return (
    <div>
      <Line 
      height={400}
      width={600}
      options={{
        maintainAspectRatio: false
        }}
      data={{
        labels: yArrayData,
        datasets: [{
          label: 'Distance from Earth in lunar distances to Avg size of meteoroid in Meters',
          data: xArrayData,
          borderColor: [
              'rgb(255, 100, 100)',
              'rgb(0, 255, 255)',
              'rgb(255, 206, 0)',
              'rgb(255, 0, 255)',
              'rgb(153, 102, 255)',
              'rgb(50, 159, 64)'
          ],
          borderWidth: 2
      }]
      }}
      />
      <Table />
    </div>
  )
}

export default ChartPage;
