import React, { useState,useContext } from 'react'
import { StoreContext } from "../../context/StoreContext";

const  Table = () =>{

  const { store } = useContext(StoreContext);  
  const [order, setOrder] = useState("ASC");
  const [data, setData] = useState(store.data);

  const sorting = (col) => {
    if (order === "ASC") {
       const sorted = [...data].sort((a,b) =>
      a[col] > b[col] ? 1 :-1);
      setOrder("DSC");
      setData(sorted);
    }
    if (order === "DSC") {
      const sorted = [...data].sort((a,b) =>
      a[col] < b[col] ? 1 :-1);
      setOrder("ASC");
      setData(sorted);
    }
  }

  // keep context api live so it will not die, look for Link or navigate so context will not die


  return (
    <div>
      <table className="striped highlight centered">
        <thead className=" teal lighten-2">
          <td>Index</td>
          <td onClick={() => sorting('name')}>
            Name
          </td>
          <td onClick={() => sorting('missDistance')}>
            Miss Distance
          </td>
          <td onClick={() => sorting('avgSize')}>
            Average Size
          </td>
          <td onClick={() => sorting('closeApproachDate')}>
            Close Approach Date
          </td>
          <td onClick={() => sorting('speed')}>
            Relative Velocity in KM/h
          </td>
          <td>URL</td>
        </thead>
          <tbody>
            {data.map((asteroid,idx) => {
              return (
                <tr 
                  className="centered"
                  key={asteroid.id}
                >
                  <td> {idx+1}</td>
                  <td>{asteroid.name}</td>
                  <td>{asteroid.missDistance}</td>
                  <td>{asteroid.avgSize}m</td>
                  <td>{asteroid.closeApproachDate}</td>
                  <td>{asteroid.speed}</td>
                  <td><a href={asteroid.url}>Link</a></td>
                </tr>
              );
            })}
          </tbody>
      </table>
    </div>
  )
}

export default Table;
