import React, { useState,useContext } from 'react'
import { StoreContext } from "../../context/StoreContext";

const  Table = () =>{

  const [order, setOrder] = useState("ASC");

  const { store } = useContext(StoreContext);  

  const data = store.data; 

  const orderedData = [...data];
  const sorting = (col) => {
    if (order === "ASC") {
      orderedData = [...data].sort((a,b) =>
      a[col] > b[col] ? 1 :-1);
      setOrder("DSC");
    }
    if (order === "DSC") {
      orderedData = [...data].sort((a,b) =>
      a[col] < b[col] ? 1 :-1);
      setOrder("ASC");
    }
  }





  //sorter function that decides how to sort stuff by name index or so whatever
  // keep context api live so it will not die, look for Link or navigate so context will not die

  /// IMPORTANT

  return (
    <div>
      <table className="striped highlight centered">
        <thead className=" teal lighten-2">
          <td>Index</td>
          <td>
            <button type="button" onClick={() => setSortedField('name')}>
              Name
            </button>
          </td>
          <td>
            <button type="button" onClick={() => setSortedField('missDistance')}>
                Miss Distance
            </button>
          </td>
          <td>
            <button type="button" onClick={() => setSortedField('avgSize')}>
              Average Size
            </button>
          </td>
          <td>
            <button type="button" onClick={() => setSortedField('closeApproachDate')}>
              Close Approach Date
            </button>
          </td>
          <td>
            <button type="button" onClick={() => setSortedField('speed')}>
              Relative Velocity in KM/h
            </button>
          </td>
          <td>URL</td>
        </thead>
          <tbody>
            {data.map((asteroid,idx) => {
              return (
                <tr 
                  className="centered"
                  key={asteroid.id}
                >
                  <td> {idx+1}</td>
                  <td>{asteroid.name}</td>
                  <td>{asteroid.missDistance}</td>
                  <td>{asteroid.avgSize}m</td>
                  <td>{asteroid.closeApproachDate}</td>
                  <td>{asteroid.speed}</td>
                  <td><a href={asteroid.url}>Link</a></td>
                </tr>
              );
            })}
          </tbody>
      </table>
    </div>
  )
}

export default Table;
