import React,{useContext} from 'react'
import { StoreContext } from "../../context/StoreContext";
import { Line } from 'react-chartjs-2'
import Chart from 'chart.js/auto';

const ChartPage = () => {

    const { store } = useContext(StoreContext);

    const data = store.data.near_earth_objects;
    let chartDataArray =[];


      Object.keys(store.data.near_earth_objects).map(day => {
          const objectsForTheDay = data[day];
          objectsForTheDay.map(day => {
            return (
                chartDataArray.push({
                    id: day.id,
                    name: day.name, 
                    missDistance: day.close_approach_data[0].miss_distance.kilometers,
                    avgSize: (day.estimated_diameter.kilometers.estimated_diameter_min + day.estimated_diameter.kilometers.estimated_diameter_max) /2
                  })
            )
        })
      })


    console.log("arr",chartDataArray)

    let orderedArray = chartDataArray.sort((a,b) => a.missDistance - b.missDistance)

  return (
    <div>
      <Line 
      height={400}
      width={600}
      options={{
        maintainAspectRatio: false
        }}
      data={{
        labels: orderedArray.map(ar => parseFloat(ar.missDistance).toFixed(2)),
        datasets: [{
          label: 'Distance from Earth in KM to Avg size of meteroid',
          data: orderedArray.map(ar => ar.avgSize.toFixed(3)),
          backgroundColor: [
              'rgba(255, 99, 132, 0.2)',
              'rgba(54, 162, 235, 0.2)',
              'rgba(255, 206, 86, 0.2)',
              'rgba(75, 192, 192, 0.2)',
              'rgba(153, 102, 255, 0.2)',
              'rgba(255, 159, 64, 0.2)'
          ],
          borderColor: [
              'rgba(255, 99, 132, 1)',
              'rgba(54, 162, 235, 1)',
              'rgba(255, 206, 86, 1)',
              'rgba(75, 192, 192, 1)',
              'rgba(153, 102, 255, 1)',
              'rgba(255, 159, 64, 1)'
          ],
          borderWidth: 1
      }]
      }}
      />
    </div>
  )
}

export default ChartPage;
