import React,{useContext} from 'react'
import { StoreContext } from "../../context/StoreContext";
import { Line } from 'react-chartjs-2'
import Chart from 'chart.js/auto';

const ChartPage = () => {

  const { store } = useContext(StoreContext);

  const data = store.data.near_earth_objects;
  let chartDataArray =[];

  Object.keys(store.data.near_earth_objects).map(day => {
    const objectsForTheDay = data[day];
    objectsForTheDay.map(day => {
      return (
        chartDataArray.push({
          id: day.id,
          name: day.name, 
          missDistance: day.close_approach_data[0].miss_distance.lunar,
          avgSize: (day.estimated_diameter.meters.estimated_diameter_min + day.estimated_diameter.meters.estimated_diameter_max) /2
        })
      )
    })
  })

  let orderedArray = chartDataArray.sort((a,b) => a.missDistance - b.missDistance)

  return (
    <div>
      <Line 
      height={400}
      width={600}
      options={{
        maintainAspectRatio: false
        }}
      data={{
        labels: orderedArray.map(ar => ar.missDistance),
        datasets: [{
          label: 'Distance from Earth in lunar distances to Avg size of meteoroid in Meters',
          data: orderedArray.map(ar => ar.avgSize.toFixed(2)),
          borderColor: [
              'rgb(255, 100, 100)',
              'rgb(0, 255, 255)',
              'rgb(255, 206, 86)',
              'rgb(255, 0, 255)',
              'rgb(153, 102, 255)',
              'rgb(255, 159, 64)'
          ],
          borderWidth: 2
      }]
      }}
      />
    </div>
  )
}

export default ChartPage;
