import React,{useContext} from 'react'
import { StoreContext } from "../../context/StoreContext";
import { Line } from 'react-chartjs-2'
import Chart from 'chart.js/auto';

const ChartPage = () => {

  const { store } = useContext(StoreContext);  
  let orderedArray = store.data.sort((a,b) => a.missDistance - b.missDistance);
  const xArrayData = orderedArray.map(arry => arry.avgSize.toFixed(2));
  const yArrayData = orderedArray.map(arry => parseFloat(arry.missDistance).toFixed(2));

  return (
    <div>

<div className="card">
          <div className='card-header'> 
            <h2 className="header">NASA API</h2>
          </div>
          <div className="card-content">

      <Line 
      height={400}
      width={600}
      options={{
        maintainAspectRatio: false
        }}
      data={{
        labels: yArrayData,
        datasets: [{
          label: 'Distance from Earth in lunar distances to Avg size of meteoroid in Meters',
          data: xArrayData,
          borderColor: [
            'rgb(255, 100, 100)',
            'rgb(0, 255, 255)',
            'rgb(255, 206, 0)',
            'rgb(255, 0, 255)',
            'rgb(153, 102, 255)',
            'rgb(50, 159, 64)'
          ],
          borderWidth: 2
        }]
      }}
      />
        </div>
        <div className="card-footer">
          <h4 className="center-align">Distance from Earth</h4>
        </div>
      </div>
    </div>
  )
}

export default ChartPage;
